export default {
  name: 'UserForm',
  props: {
    modelValue: {
      type: Boolean,
      default: false
    },
    user: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      valid: false,
      formData: {
        username: '',
        password: '',
        roles: [],
        timezone: 'UTC',
        active: true
      },
      availableRoles: ['admin', 'manager', 'user', 'support', 'developer']
    };
  },
  computed: {
    dialog: {
      get() {
        return this.modelValue;
      },
      set(value) {
        this.$emit('update:modelValue', value);
      }
    },
    isEdit() {
      return !!this.user?._id;
    }
  },
  watch: {
    modelValue(val) {
      if (val) {
        this.initForm();
      }
    }
  },
  methods: {
    initForm() {
      if (this.user) {
        this.formData = {
          _id: this.user._id,
          username: this.user.username,
          password: this.user.password,
          roles: [...this.user.roles],
          timezone: this.user.preferences.timezone,
          active: this.user.active
        };
      } else {
        this.formData = {
          username: '',
          password: '',
          roles: [],
          timezone: 'UTC',
          active: true
        };
      }
      this.$nextTick(() => {
        if (this.$refs.form) {
          this.$refs.form.resetValidation();
        }
      });
    },
    saveUser() {
      if (!this.valid) return;
      this.$emit('save', this.formData);
    }
  }
};
